import React from 'react'
import { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { db } from '../firebase-config'
import { collection, getDocs, query, where } from 'firebase/firestore'

const Album = () => {
  const [photos, setPhotos] = useState([])
  const photosCollectionRef = collection(db, "photos")
  const { albumid } = useParams()

  const qPhotos = query(photosCollectionRef, where("albumid", "==", `${albumid}`))

  useEffect(() => {
    const getPhotos = async () => {
        const data = await getDocs(qPhotos)
        setPhotos(data.docs.map((doc) => ({...doc.data(), id: doc.id})))
        console.log(photos)

    }
    getPhotos()
}, [])





  return (
    <div>
        <h1 className="text-headBlue font-merriweather text-4xl text-center mt-10">{albumid}</h1>
        {/* TODO: Line breaks, props: dynamic album title, description, and content */}
        <p className="text-darkOrange font-merriweather text-xl text-center my-3">Album description</p>
        <section className="grid md:grid-cols-3 grid-cols-2 gap-2 md:mx-20 mx-5">
            {photos.map((photo) => {
                return (
                        <img src={photo.source} alt={photo.alt} title={photo.alt} 
                        className="cursor-pointer"/>
                )
            })}
        </section>
    </div>
  )
}

export default Album
